queries.querystring:
SELECT DATE_FORMAT(date_field, '%Y-%m-%d') 'DATE_FIELD' ,ifnull(assets_planned,0) 'ASSETS_PLAN' , assets_count 'ASSETS_THEO' , round((assets_count*0.7),0) 'ASSETS_REAL' , round((assets_count*0.7),0) - ifnull(assets_planned,0) 'ASSETS_AVAIL' FROM ( SELECT date_field, date_tk , COUNT(ASSET) AS assets_count FROM mrqr_assets ma, damrsysl_calendar_dates WHERE ma.HIGHER_ASSET IS NULL and ma.function like 'pFUNCTION' AND ma.STATUS NOT IN('D','A','B','E','O') AND (ma.STORE NOT LIKE('%VE') OR ma.STORE IS NULL) AND date_field >= DATE_SUB(CURRENT_DATE, INTERVAL 1 month) AND date_field <= DATE_ADD(CURRENT_DATE, INTERVAL 1 month) AND MFRDATE <= date_field GROUP BY date_tk ) exist_sum left join ( SELECT date_tk , sum(mum.nbr_of_assets) AS assets_planned from mrqr_mission_units_material mum INNER JOIN mrqr_mission_participating_units mpu on mum.omu_id=mpu.omu_id, damrsysl_calendar_dates WHERE mum.function like 'pFUNCTION' AND date_field >= DATE_SUB(CURRENT_DATE, INTERVAL 1 month) AND date_field < DATE_ADD(CURRENT_DATE, INTERVAL 1 month) AND date_field >= mpu.start_date AND date_field < mpu.end_date GROUP BY date_field, date_tk ) plan_sum on plan_sum.date_tk=exist_sum.date_tk
WHERE (length(replace(replace('pFUNCTION','%',''),'_',''))>=3
)
